import { Component, OnInit } from '@angular/core';

@Component({
  selector: 'app-root',
  template: `
    <h1>Microfrontend</h1>
  `,
})
export class AppComponent implements OnInit {
  ngOnInit() {
    // Send a message to the main app
    window.opener.postMessage('Hello from microfrontend!', "*");
  }
}
